mapper.insert:
INSERT INTO USER_TABLE
(
    USER_ID,
    USER_NAME
)
VALUES
(
    #{USER_ID},
    #{USER_NAME}
)

mapper.select:
SELECT USER_ID, USER_PW, USER_NAME,USER_VERIFY
FROM USER_TABLE
WHERE USER_ID = #{USER_ID}

    AND USER_PW = #{USER_PW}

SELECT USER_ID, USER_NAME,USER_VERIFY
FROM USER_TABLE
WHERE USER_ID = #{USER_ID}

SELECT USER_ID, USER_PHONE, USER_VERIFY, USER_NAME, USER_BIRTH, USER_GENDER, DATE_FORMAT(CREATED_DT, "%Y %c/%e %r") AS CREATED_DT FROM USER_TABLE WHERE 1= 1
AND USER_ID LIKE CONCAT('%',#{USER_ID},'%')
AND USER_NAME LIKE CONCAT('%',#{USER_NAME},'%')
AND USER_VERIFY = ${USER_VERIFY}
LIMIT ${PAGE_START}, ${PER_PAGE_NUM}

SELECT COUNT(*) AS TOTAL_COUNT FROM USER_TABLE WHERE 1= 1
AND USER_ID LIKE CONCAT('%',#{USER_ID},'%')
AND USER_NAME LIKE CONCAT('%',#{USER_NAME},'%')
AND USER_VERIFY = ${USER_VERIFY}
LIMIT ${PAGE_START}, ${PER_PAGE_NUM}

mapper.update:
UPDATE USER_TABLE 
SET USER_NAME = #{USER_NAME},
USER_PHONE = #{USER_PHONE},
USER_VERIFY = #{USER_VERIFY},
USER_GENDER = #{USER_GENDER}
WHERE USER_ID = #{USER_ID}
